const colors = require('colors');

const moralisUtils = require('./moralis/utils/moralisUtils');

async function main() {
  colors.enable();
  try {
    console.log('-------------Connecting with Moralis Server to set discord settings-------------'.green);
    console.group();
    const options = { useMasterKey: true };
    await moralisUtils.initialize(options);
    await moralisUtils.setDiscordSettings(options);
    console.groupEnd();
    console.log('-------------Discord settings was set successfully-------------'.green);
  } catch (error) {
    console.error(error);
    throw error;
  }
}

main()
  .then(() => process.exit(0))
  .catch(() => {
    process.exit(1);
  });
