module.exports.deployLilVillains = async ({ deployer, villainMinter, royaltyAddressToSet, baseUri, description }) => {
  const DynamicMetadataLib = await ethers.getContractFactory('DynamicMetadata', deployer);
  const dynamicMetadataLib = await DynamicMetadataLib.deploy();

  const LilVillains = await ethers.getContractFactory('LilVillains', {
    signer: deployer,
    libraries: {
      DynamicMetadata: dynamicMetadataLib.address,
    },
  });

  const lilVillains = await LilVillains.deploy(villainMinter, royaltyAddressToSet, baseUri, description);
  await lilVillains.deployed();
  return lilVillains;
};
