const moralisUtils = require('./utils/moralisUtils');
const utils = require('./utils/utils');

const options = { useMasterKey: true };

async function main() {
  console.log('Splitting Full Name ...');
  console.time('Process');
  console.group();

  await moralisUtils.initialize(options);
  await moralisUtils.splitFullName(options);

  console.groupEnd();
  utils.printElapsedTime('Process');
  console.log('Splitted successfully!');
}

exports.main = main;

main();
