const prompt = require('prompt');
prompt.start();

const schema = {
  properties: {
    isOk: {
      description: 'Are settings ok?(y/n)', // Prompt displayed to the user. If not supplied name will be used.
      type: 'string', // Specify the type of input to expect.
      pattern: /^(?:Y|N|Yes|No|yes|no|y|n)+$/,
      message: "response must be 'Yes/Y/yes/y' or 'No/N/no/n'",
      required: true, // If true, value entered must be non-empty.
    },
  },
};
exports.askIsOk = async (settings) => {
  for (const setting of settings) {
    console.table(setting);
  }
  const response = await prompt.get(schema);
  if (response.isOk.toLowerCase().startsWith('n')) throw 'Process canceled';
};
