/**
 * returns received parameters(amount and address) packed in a signature
 *
 * @param {number} amount available claimed amount
 * @param {address} address enabled address to claim
 *
 * @returns {ethers.BytesLike} data signed
 */
async function sign(types, value, signer, contractAddress, signingDomainName, signingDomainVersion) {
  const domain = {
    name: signingDomainName,
    version: signingDomainVersion,
    chainId: hre.network.config.chainId,
    verifyingContract: contractAddress,
  };
  const signature = await signer._signTypedData(domain, types, value);
  return signature;
}
exports.signerService = {
  sign,
};
